import React, {useState, useEffect} from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import {readDeck} from "../utils/api/index";
import StudyFlip from "./StudyFlip";

function Study(){
    const params = useParams();
    const history = useHistory();
    const [currDeck, setCurrDeck] = useState(null);
    const [currCard, setCurrCard] = useState(null);
    const [count, setCount] = useState(1);
    const [side, setSide] = useState(null);
    const [studyCount, setStudyCount] = useState(0);
    const [studyCard, setStudyCard] = useState(null);

    useEffect(() => {
        async function loadDeck(){
            setCurrDeck([]);
            setCurrCard([]);
            try{
                const event = await readDeck(params.deckId);
                setCurrDeck(event);
                const {cards} = event;
                setCurrCard(event);
            } catch (error) {
                console.log(error);
            }
        }
        loadDeck();
    }, [params])

    const handleAdd = (event) => {
        event.preventDefault();
        history.push(`/decks/${params.deckId}/cards/new`)
    }

    const handleCardFlip = (event) => {
        event.preventDefault();
        setSide(!side);
    }

    const handleNextCard = (event) => {
        event.preventDefault();
        setSide(!side);
        setCount((currentCard) => currentCard++);
        setStudyCard((currentCard) => currentCard++);
        if(currCard.length <= count){
            if(window.confirm("Restart Cards?")){
                setCount(1);
                setStudyCard(0);
                history.push(`/decks/${params.deckId}/study`)
            } else {
                history.push("/");
            }
        }
    }



    if(currDeck){
        return(
            <div>
                <div>
                    <Link to="/">Home</Link> / <Link to={`/decks/${currDeck.id}`}>{currDeck.name}</Link> / Study
                </div>
                <div>
                    <h1>{currDeck.name}: Study</h1>
                </div>
            <StudyFlip currCard/>
            </div>
        );
    }
    return<h2>Loading Please Wait...</h2>
}

export default Study;
