    const params = useParams();

    const [currDeck, setCurrDeck] = useState(null);
    const [name, setName] = useState("");
    const [description, setDescription] = ("");
    
    return (
        <div>
            <div>
                <Link to={"/"}>Home</Link> / {"insert name of deck being edited"}
            </div>
            <br />
            <h2>Edit Deck</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name
                    <br />
                    <input 
                        type="text"
                        required
                        onChange={handleFormChange}
                    />
                </label>
                <br />
                <label>
                    Description
                    <br />
                    <textarea 
                        type="text"
                        required
                        onChange={handleFormChange}
                    />
                </label>
            </form>
        </div>
    );
}
