import React, {useState} from "react";
import {Link, useHistory} from "react-router-dom";
import {createDeck} from "../utils/api/index";

function CreateDeck() {
    const history = useHistory();
    // const [name, setName] = useState("Deck Name");
    // const [description, setDescription] = useState("Brief description of the deck");

    const initialFormState = {
        name: "Deck Name",
        description: "Brief description of the deck",
    };

    const [formState, setFormState] = useState(initialFormState);

    const handleFormChange = (event) => {
        setFormState({
            ...formState, 
            [event.target.name]: event.target.value,
        });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        async function Submit() {
            const createNewDeck = await createDeck({name, description});
            return createNewDeck;
        }
        Submit();
        history.push(`decks/${Submit().id}`);
    }

    const handleCancel = (event) => {
        event.preventDefault();
        history.push("/");
    }

    return (
        <div>
            <div>
                <Link to={"/"}>Home</Link> / <p>Create Deck</p>
            </div>
            <h1>Create Deck</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name
                    <input
                        type="text"
                        onChange={handleFormChange}
                        value={name}
                    />
                </label>
                <label>
                    Description
                    <input
                        type="textarea"
                        onChange={handleFormChange}
                        value={description}
                    />
                </label>
            </form>
        </div>
    );
}

export default CreateDeck;
