import React from "react";
import {Link} from "react-router-dom";
import { readDeck } from "../utils/api/index";

function EditDeck() {

    return (
        <div>
            <div>
                <Link to={"/"}>Home</Link> / {"insert name of deck being edited"}
            </div>
            <br />
            <h2>Edit Deck</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name
                    <br />
                    <input 
                        type="text"
                        required
                        onChange={handleFormChange}
                    />
                </label>
                <br />
                <label>
                    Description
                    <br />
                    <textarea 
                        type="text"
                        required
                        onChange={handleFormChange}
                    />
                </label>
            </form>
        </div>
    );
}
